// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function Home() {
  const { user } = useAuth();

  return (
    <div className="home-page">
      <div className="hero-section">
        <h1>Welcome to Our Bookstore</h1>
        <p>Discover your next favorite book with us</p>
        <Link to="/books" className="cta-button">Browse Books</Link>
      </div>
      
      <div className="features">
        <div className="feature-item">
          <h3>Wide Selection</h3>
          <p>Thousands of books across all genres</p>
        </div>
        <div className="feature-item">
          <h3>Fast Delivery</h3>
          <p>Get your books delivered quickly</p>
        </div>
        <div className="feature-item">
          <h3>Great Prices</h3>
          <p>Competitive prices and regular discounts</p>
        </div>
      </div>

      {user && (
        <div className="welcome-user">
          <h2>Hello, {user.name}!</h2>
          <Link to="/profile">View Your Profile</Link>
        </div>
      )}
    </div>
  );
}

export default Home;
