// src/components/admin/BookManager.js
import React, { useState, useEffect } from 'react';
import * as api from '../../services/api';

function BookManager() {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '', price: '' });
  const [editingBook, setEditingBook] = useState(null);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const response = await api.getBooks();
    setBooks(response.data);
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    await api.addBook(newBook);
    setNewBook({ title: '', author: '', price: '' });
    fetchBooks();
  };

  const handleEditBook = async (book) => {
    await api.updateBook(book.id, book);
    setEditingBook(null);
    fetchBooks();
  };

  const handleDeleteBook = async (id) => {
    await api.deleteBook(id);
    fetchBooks();
  };

  return (
    <div className="book-manager">
      <h3>Manage Books</h3>
      
      {/* Add Book Form */}
      <form onSubmit={handleAddBook}>
        <input
          value={newBook.title}
          onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
          placeholder="Title"
          required
        />
        <input
          value={newBook.author}
          onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
          placeholder="Author"
          required
        />
        <input
          type="number"
          value={newBook.price}
          onChange={(e) => setNewBook({ ...newBook, price: e.target.value })}
          placeholder="Price"
          required
        />
        <button type="submit">Add Book</button>
      </form>

      {/* Book List */}
      {books.map((book) => (
        <div key={book.id} className="book-admin-item">
          {editingBook?.id === book.id ? (
            <div>
              <input
                value={editingBook.title}
                onChange={(e) => setEditingBook({ ...editingBook, title: e.target.value })}
              />
              <input
                value={editingBook.author}
                onChange={(e) => setEditingBook({ ...editingBook, author: e.target.value })}
              />
              <input
                type="number"
                value={editingBook.price}
                onChange={(e) => setEditingBook({ ...editingBook, price: e.target.value })}
              />
              <button onClick={() => handleEditBook(editingBook)}>Save</button>
              <button onClick={() => setEditingBook(null)}>Cancel</button>
            </div>
          ) : (
            <div>
              <span>{book.title} - {book.author} - ${book.price}</span>
              <button onClick={() => setEditingBook(book)}>Edit</button>
              <button onClick={() => handleDeleteBook(book.id)}>Delete</button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default BookManager;
