import React, { createContext, useState, useEffect, useContext } from 'react';
import * as api from '../services/api';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isEmployee, setIsEmployee] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]); // Store cart items per user

  useEffect(() => {
    const storedUser = sessionStorage.getItem('user');
    console.log('Stored user in sessionStorage:', storedUser);
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        console.log('Parsed user:', parsedUser);
        if (parsedUser && parsedUser.role) {
          parsedUser.role = parsedUser.role.toLowerCase();
          setUser(parsedUser);
          setIsAdmin(parsedUser.role === 'admin');
          setIsEmployee(parsedUser.role === 'employee');
          // Load cart items for this user from sessionStorage
          const storedCart = sessionStorage.getItem(`cart_${parsedUser.id}`);
          if (storedCart) {
            setCartItems(JSON.parse(storedCart));
          }
          console.log('User set successfully:', parsedUser);
        } else {
          console.log('No role found in stored user, clearing sessionStorage');
          sessionStorage.removeItem('user');
        }
      } catch (error) {
        console.error('Error parsing stored user:', error);
        sessionStorage.removeItem('user');
      }
    } else {
      console.log('No user found in sessionStorage');
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    // Save cart items to sessionStorage for the current user
    if (user) {
      sessionStorage.setItem(`cart_${user.id}`, JSON.stringify(cartItems));
    }
  }, [cartItems, user]);

  const login = async (credentials) => {
    try {
      const response = await api.loginUser(credentials);
      const userData = response.data;
      console.log('User data from API:', userData);
      if (!userData || !userData.role) {
        throw new Error('User role not provided by API');
      }
      userData.role = userData.role.toLowerCase();
      setUser(userData);
      setIsAdmin(userData.role === 'admin');
      setIsEmployee(userData.role === 'employee');
      sessionStorage.setItem('user', JSON.stringify(userData));
      // Load user's cart from sessionStorage
      const storedCart = sessionStorage.getItem(`cart_${userData.id}`);
      setCartItems(storedCart ? JSON.parse(storedCart) : []);
      return userData;
    } catch (error) {
      console.error('Login error:', error);
      throw new Error('Login failed');
    }
  };

  const logout = () => {
    setUser(null);
    setIsAdmin(false);
    setIsEmployee(false);
    setCartItems([]); // Clear cart on logout
    sessionStorage.removeItem('user');
    // Clear all cart items for all users (optional, depending on requirements)
    Object.keys(sessionStorage).forEach((key) => {
      if (key.startsWith('cart_')) {
        sessionStorage.removeItem(key);
      }
    });
  };

  const addToCart = (book) => {
    const existingItem = cartItems.find((item) => item.id === book.id);
    if (existingItem) {
      setCartItems(
        cartItems.map((item) =>
          item.id === book.id ? { ...item, quantity: item.quantity + 1 } : item
        )
      );
    } else {
      setCartItems([...cartItems, { ...book, quantity: 1 }]);
    }
  };

  if (isLoading) {
    return null;
  }

  return (
    <AuthContext.Provider
      value={{
        user,
        isAdmin,
        isEmployee,
        login,
        logout,
        isLoading,
        cartItems,
        setCartItems,
        addToCart,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
