// src/components/admin/EmployeeManager.js
import React, { useState, useEffect } from 'react';
import * as api from '../../services/api';

function EmployeeManager() {
  const [employees, setEmployees] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await api.getEmployees();
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleAddEmployee = async () => {
    try {
      const employeeData = { ...formData, role: 'employee' }; // Thêm role: 'employee'
      await api.addEmployee(employeeData);
      setShowForm(false);
      setFormData({ name: '', email: '', password: '' });
      fetchEmployees();
    } catch (error) {
      console.error('Error adding employee:', error);
    }
  };

  const handleEditEmployee = async () => {
    try {
      const employeeData = { ...formData, role: 'employee' }; // Giữ role khi sửa
      await api.updateEmployee(editingEmployee.id, employeeData);
      setEditingEmployee(null);
      setFormData({ name: '', email: '', password: '' });
      fetchEmployees();
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDeleteEmployee = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await api.deleteEmployee(id);
        fetchEmployees();
      } catch (error) {
        console.error('Error deleting employee:', error);
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingEmployee) {
      handleEditEmployee();
    } else {
      handleAddEmployee();
    }
  };

  return (
    <div className="employee-manager">
      <div className="manager-header">
        <h2>Employee Management</h2>
        <button className="add-employee-btn" onClick={() => setShowForm(true)}>
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <line x1="12" y1="5" x2="12" y2="19" />
            <line x1="5" y1="12" x2="19" y2="12" />
          </svg>
          Add New Employee
        </button>
      </div>

      {(showForm || editingEmployee) && (
        <form className="employee-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              required={!editingEmployee}
            />
          </div>
          <div className="form-actions">
            <button type="submit" className="submit-btn">
              {editingEmployee ? 'Update Employee' : 'Add Employee'}
            </button>
            <button
              type="button"
              className="cancel-btn"
              onClick={() => {
                setShowForm(false);
                setEditingEmployee(null);
                setFormData({ name: '', email: '', password: '' });
              }}
            >
              Cancel
            </button>
          </div>
        </form>
      )}

      <div className="employees-table">
        <div className="table-header">
          <span>Name</span>
          <span>Email</span>
          <span>Actions</span>
        </div>
        {employees.map((employee) => (
          <div key={employee.id} className="table-row">
            <span>{employee.name}</span>
            <span>{employee.email}</span>
            <div className="table-actions">
              <button
                className="edit-employee-btn"
                onClick={() => {
                  setEditingEmployee(employee);
                  setFormData({ name: employee.name, email: employee.email, password: '' });
                }}
              >
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" />
                  <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z" />
                </svg>
              </button>
              <button
                className="delete-employee-btn"
                onClick={() => handleDeleteEmployee(employee.id)}
              >
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M3 6h18M8 6V4a2 2 0 0 1 2-2h4a2 0 0 1 2 2v2m3 0v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6h14z"/>
                </svg>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EmployeeManager;
