// src/pages/Books.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import * as api from '../services/api';
import { useTranslation } from 'react-i18next';

function Books({ cartItems, setCartItems, addToCart }) {
  const [books, setBooks] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const { t } = useTranslation();

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await api.getBooks();
        setBooks(response.data);
        const uniqueGenres = [...new Set(response.data.map(book => book.genre).filter(Boolean))];
        setGenres(uniqueGenres);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };
    fetchBooks();
  }, []);

  const filteredBooks = books.filter(book => {
    const matchesGenre = selectedGenre === 'all' || book.genre === selectedGenre;
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesGenre && matchesSearch;
  });

  return (
    <div className="books-page">
      <h1>{t('books')}</h1>
      <div className="books-controls">
        <div className="search-bar">
          <input
            type="text"
            placeholder={t('search_placeholder')}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="genre-filter">
          <label>{t('filter_by_genre')}: </label>
          <select
            value={selectedGenre}
            onChange={(e) => setSelectedGenre(e.target.value)}
          >
            <option value="all">{t('all_genres')}</option>
            {genres.map((genre, index) => (
              <option key={index} value={genre}>{genre}</option>
            ))}
          </select>
        </div>
      </div>
      <div className="books-grid">
        {filteredBooks.map((book) => (
          <div key={book.id} className="book-card">
            <Link to={`/book/${book.id}`}>
              <img src={book.coverImage || 'https://via.placeholder.com/150x200'} alt={book.title} />
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <p>{book.price.toLocaleString('vi-VN')} VND</p>
              <p>{t('quantity')}: {book.quantity || 0}</p>
            </Link>
            {book.quantity > 0 ? (
              <button
                className="add-to-cart-btn"
                onClick={() => addToCart(book)}
              >
                {t('add_to_cart')}
              </button>
            ) : (
              <p className="out-of-stock">{t('out_of_stock')}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Books;
