import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTranslation } from '../context/TranslationContext';

function Login() {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const { login } = useAuth();
  const { t } = useTranslation();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
  e.preventDefault();
  setError('');
  try {
    const response = await loginAccount(credentials);
    const userData = response.data;

    const role = userData.role?.toLowerCase();
    if (role === 'admin') {
      navigate('/admin');
    } else if (role === 'employee') {
      navigate('/employee');
    } else {
      navigate('/');
    }
  } catch (err) {
    const message = err.response?.data?.error || t('invalid_credentials') || 'Login failed';
    setError(message);
  }
};

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>{t('login')}</h2>
        <p className="auth-subtitle">{t('sign_in')}</p>
        {error && <div className="error-message">{error}</div>}
        <form className="auth-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label>{t('email')}</label>
            <input
              type="email"
              value={credentials.email}
              onChange={(e) => setCredentials({ ...credentials, email: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>{t('password')}</label>
            <input
              type="password"
              value={credentials.password}
              onChange={(e) => setCredentials({ ...credentials, password: e.target.value })}
              required
            />
          </div>
          <button type="submit" className="auth-button">{t('login')}</button>
        </form>
        <div className="auth-links">
          <Link to="/forgot-password" className="forgot-password-link">
            <button className="forgot-password-btn">{t('forgot_password')}</button>
          </Link>
          <div className="auth-link">
            {t('dont_have_account')} <Link to="/register">{t('register')}</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
