// src/components/common/Footer.js
import React from 'react';
import { useTranslation } from '../../context/TranslationContext';

function Footer() {
  const { t } = useTranslation();
  return (
    <footer className="footer">
      <div className="footer-content">
        <div className="footer-section">
          <h4>{t('about_us')}</h4>
          <p> {t('Your trusted online bookstore since 2025')}</p>
        </div>
        <div className="footer-section">
          <h4>{t('quick_links')}</h4>
          <ul>
            <li><a href="/books">{t('books')}</a></li>
            <li><a href="/profile">{t('profile')}</a></li>
            <li><a href="/cart">{t('cart')}</a></li>
          </ul>
        </div>
        <div className="footer-section">
          <h4> {t('contact')} </h4>
          <p> Email : support@bookstore.com  </p>
          <p>{t('phone')} : (123) 456-7890 </p>
        </div>
      </div>
      <div className="footer-bottom">
        <p class = "center"> &copy;{t('2025 Bookstore. All rights reserved.')}</p>
      </div>
    </footer>
  );
}

export default Footer;
