// src/pages/Books.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import * as api from '../services/api';

function Books({ cartItems, setCartItems, addToCart }) {
  const [books, setBooks] = useState([]);
  const [genres, setGenres] = useState([]); // Để lưu danh sách thể loại
  const [selectedGenre, setSelectedGenre] = useState('all'); // Thể loại đang chọn

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await api.getBooks();
        setBooks(response.data);
        // Lấy danh sách thể loại duy nhất
        const uniqueGenres = [...new Set(response.data.map(book => book.genre).filter(Boolean))];
        setGenres(uniqueGenres);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };
    fetchBooks();
  }, []);

  const filteredBooks = selectedGenre === 'all'
    ? books
    : books.filter(book => book.genre === selectedGenre);

  return (
    <div className="books-page">
      <h1>Books</h1>
      <div className="genre-filter">
        <label>Filter by Genre: </label>
        <select
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
        >
          <option value="all">All Genres</option>
          {genres.map((genre, index) => (
            <option key={index} value={genre}>{genre}</option>
          ))}
        </select>
      </div>
      <div className="books-grid">
        {filteredBooks.map((book) => (
          <div key={book.id} className="book-card">
            <Link to={`/book/${book.id}`}>
              <img src={book.coverImage || 'https://via.placeholder.com/150x200'} alt={book.title} />
              <h3>{book.title}</h3>
              <p>{book.author}</p>
              <p>{book.price.toLocaleString('vi-VN')} VND</p>
              <p>Quantity: {book.quantity || 0}</p> {/* Hiển thị số lượng */}
            </Link>
            {book.quantity > 0 ? (
              <button
                className="add-to-cart-btn"
                onClick={() => addToCart(book)}
              >
                Add to Cart
              </button>
            ) : (
              <p className="out-of-stock">Out of Stock</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Books;
