// src/pages/Home.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTranslation } from '../context/TranslationContext';

function Home() {
  const { t } = useTranslation();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/books?search=${encodeURIComponent(searchQuery)}`);
    }
  };

  return (
    <div className="home-page">
      <div className="hero-section">
        <h1>{t('welcome_to_our_bookstore')}</h1>
        <p>{t('discover_your_next_favorite_book_with_us')}</p>
        
        <form className="search-form" onSubmit={handleSearch}>
          <div className="search-container">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search for books..."
              className="search-input"
            />
            <button type="submit" className="search-button">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                <circle cx="11" cy="11" r="8" />
                <line x1="21" y1="21" x2="16.65" y2="16.65" />
              </svg>
            </button>
          </div>
        </form>
        
        <Link to="/books" className="cta-button">{t('browse_books')}</Link>
      </div>
      
      <div className="features">
        <div className="feature-item">
          <h3>{t('Wide Selection')}</h3>
          <p>{t('Thousands of books across all genres')}</p>
        </div>
        <div className="feature-item">
          <h3>{t('Fast Delivery')}</h3>
          <p>{t('Get your books delivered quickly')}</p>
        </div>
        <div className="feature-item">
          <h3>{t('Great Prices')}</h3>
          <p>{t('Competitive prices and regular discounts')}</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
