import axios from 'axios';

//Định nghĩa base URL cho API
const API_BASE = 'http://127.0.0.1:8000/api/';

// === ACCOUNT API ===
export const getAccounts = () => axios.get(`${API_BASE}accounts/`);
export const createAccount = (data) => axios.post(`${API_BASE}accounts/`, data);
export const deleteAccount = (id) => axios.delete(`${API_BASE}accounts/${id}/`);


// === BOOK API ===
export const getBooks = () => axios.get(`${API_BASE}books/`);
export const createBook = (data) => axios.post(`${API_BASE}books/`, data);
export const deleteBook = (id) => axios.delete(`${API_BASE}books/${id}/`);
export const getBookDetail = (id) => axios.get(`${API_BASE}book/${id}/`);


// === CATEGORY API ===
export const getCategories = () => axios.get(`${API_BASE}categories/`);
export const createCategory = (data) => axios.post(`${API_BASE}categories/`, data);
export const deleteCategory = (id) => axios.delete(`${API_BASE}categories/${id}/`);


// === ORDER API ===
export const getOrders = () => axios.get(`${API_BASE}orders/`);
export const createOrder = (data) => axios.post(`${API_BASE}orders/`, data);
export const deleteOrder = (id) => axios.delete(`${API_BASE}orders/${id}/`);


// === PAYMENT API ===
export const getPayments = () => axios.get(`${API_BASE}payments/`);
export const createPayment = (data) => axios.post(`${API_BASE}payments/`, data);
export const deletePayment = (id) => axios.delete(`${API_BASE}payments/${id}/`);


// === STATUS ORDER API ===
export const getStatusOrders = () => axios.get(`${API_BASE}status-orders/`);
export const createStatusOrder = (data) => axios.post(`${API_BASE}status-orders/`, data);
export const deleteStatusOrder = (id) => axios.delete(`${API_BASE}status-orders/${id}/`);


// === USER API ===
export const getUsers = () => axios.get(`${API_BASE}users/`);
export const createUser = (data) => axios.post(`${API_BASE}users/`, data);
export const deleteUser = (id) => axios.delete(`${API_BASE}users/${id}/`);


// === EMPLOYEE API ===
// Lấy danh sách nhân viên
export const getEmployees = () => {
  return axios.get(`${API_BASE}get_employees/`);
};

// Thêm nhân viên
export const addEmployee = (data) => {
  return axios.post(`${API_BASE}add_employee/`, data);
};

// Sửa nhân viên
export const editEmployee = (id, data) => {
  return axios.put(`${API_BASE}${id}/edit_employee/`, data);
};

// Xoá nhân viên
export const deleteEmployee = (id) => {
  return axios.delete(`${API_BASE}${id}/delete_employee/`);
};

// === PASSWORD RESET ===
export const resetPassword = (email) => axios.post(`${API_BASE}reset-password/`, { email });
