// src/pages/Checkout.js
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import * as api from '../services/api';
import { useAuth } from '../context/AuthContext';
import { useTranslation } from '../context/TranslationContext';

function Checkout({ setCartItems }) {
  const location = useLocation();
  const { user } = useAuth();
  const { t } = useTranslation();
  const { cartItems } = location.state || { cartItems: [] };
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    phone: '',
    paymentMethod: 'cash',
    paymentDate: new Date().toISOString().split('T')[0]
  });
  const [error, setError] = useState('');

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      setError('Please log in to place an order.');
      return;
    }

    try {
      const orderData = {
        customerId: user.id,
        customerName: formData.name,
        items: cartItems,
        total: total,
        status: 'Pending Confirmation',
        shippingInfo: {
          address: formData.address,
          phone: formData.phone,
          paymentMethod: formData.paymentMethod,
          paymentDate: formData.paymentDate
        }
      };
      await api.createOrder(orderData);
      alert('Order placed successfully! Waiting for employee confirmation.');
    } catch (error) {
      console.error('Error placing order:', error);
      setError('Failed to place order. Please try again.');
    }
  };

  return (
    <div className="checkout-page">
      <h1>{t('checkout')}</h1>
      {error && <div className="error-message">{error}</div>}
      <div className="checkout-container">
        <div className="order-summary">
          <h3>{t('order_summary')}</h3>
          {cartItems.map(item => (
            <div key={item.id} className="summary-item">
              <span>{item.title} (x{item.quantity})</span>
              <span>{(item.price * item.quantity).toLocaleString('vi-VN')} VND</span>
            </div>
          ))}
          <div className="total">
            <strong>Total:</strong>
            <strong>{total.toLocaleString('vi-VN')} VND</strong>
          </div>
        </div>
        
        <form onSubmit={handleSubmit} className="checkout-form">
          <h3>{t('shipping_info')}</h3>
          <div className="form-group">
            <label>{t('full_name')}</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>{t('address')}</label>
            <input
              type="text"
              value={formData.address}
              onChange={(e) => setFormData({ ...formData, address: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>{t('phone')}</label>
            <input
              type="tel"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              required
            />
          </div>
          <div className="form-group">
            <label>{t('payment_method')}</label>
            <select
              value={formData.paymentMethod}
              onChange={(e) => setFormData({ ...formData, paymentMethod: e.target.value })}
            >
              <option value="cash">{t('cash')}</option>
              <option value="card">{t('card')}</option>
            </select>
          </div>
          <div className="form-group">
            <label>{t('payment_date')}</label>
            <input
              type="date"
              value={formData.paymentDate}
              onChange={(e) => setFormData({ ...formData, paymentDate: e.target.value })}
              required
            />
          </div>
          <button type="submit" className="place-order-btn">{t('place_order')}</button>
        </form>
      </div>
    </div>
  );
}

export default Checkout;
