// src/components/employee/OrderManager.js
import React, { useState, useEffect } from 'react';
import * as api from '../../services/api';

function OrderManager() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await api.getOrders();
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await api.updateOrderStatus(orderId, { status: newStatus });
      fetchOrders();
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  return (
    <div className="order-manager">
      <h2>Order Management</h2>
      <div className="orders-table">
        <div className="table-header">
          <span>Order ID</span>
          <span>Customer</span>
          <span>Total</span>
          <span>Status</span>
          <span>Actions</span>
        </div>
        {orders.length === 0 ? (
          <div className="table-row">No orders available</div>
        ) : (
          orders.map((order) => (
            <div key={order.id} className="table-row">
              <span>{order.id}</span>
              <span>{order.customerName || 'Unknown'}</span>
              <span>
                {(order.total || 0).toLocaleString('vi-VN')} VND {/* Xử lý khi total undefined */}
              </span>
              <span>{order.status || 'Pending'}</span>
              <div className="table-actions">
                <select
                  className="status-select"
                  value={order.status || 'Pending Confirmation'}
                  onChange={(e) => handleStatusChange(order.id, e.target.value)}
                >
                  <option value="Pending Confirmation">Pending Confirmation</option>
                  <option value="Confirmed">Confirmed</option>
                  <option value="Shipped">Shipped</option>
                  <option value="Delivered">Delivered</option>
                  <option value="Cancelled">Cancelled</option>
                </select>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default OrderManager;
