// src/pages/EmployeeDashboard.js
import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import BookManager from '../components/employee/BookManager';
import OrderManager from '../components/employee/OrderManager';
import UserManager from '../components/employee/UserManager';
import { useTranslation } from 'react-i18next';

function EmployeeDashboard() {
  const { t } = useTranslation();
  const { isEmployee } = useAuth();
  const [activeTab, setActiveTab] = useState('books'); // Quản lý tab hiện tại

  if (!isEmployee) {
    return <Navigate to="/" />;
  }

  return (
    <div className="employee-dashboard">
      <div className="employee-header">
        <h1>Employee Dashboard</h1>
        <p>Manage books, orders, and users</p>
      </div>
      <div className="employee-tabs">
        <button
          className={`tab-btn ${activeTab === 'books' ? 'active' : ''}`}
          onClick={() => setActiveTab('books')}
        >
          Book Manager
        </button>
        <button
          className={`tab-btn ${activeTab === 'orders' ? 'active' : ''}`}
          onClick={() => setActiveTab('orders')}
        >
          Order Manager
        </button>
        <button
          className={`tab-btn ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          User Manager
        </button>
      </div>
      <div className="employee-content">
        {activeTab === 'books' && <BookManager />}
        {activeTab === 'orders' && <OrderManager />}
        {activeTab === 'users' && <UserManager />}
      </div>
    </div>
  );
}

export default EmployeeDashboard;
