// src/pages/BookDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import * as api from '../services/api';

function BookDetail({ addToCart }) {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await api.getBookById(id);
        setBook(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching book:', error);
        setLoading(false);
      }
    };
    fetchBook();
  }, [id]);

  if (loading) return <div className="loading">Loading...</div>;
  if (!book) return <div className="not-found">Book not found</div>;

  return (
    <div className="book-detail-page">
      <div className="book-detail-container">
        <div className="book-image">
          <img 
            src={book.coverImage || 'https://via.placeholder.com/300x400'} 
            alt={book.title} 
          />
        </div>
        <div className="book-info">
          <h1>{book.title}</h1>
          <div className="book-details">
            <p><strong>Author:</strong> {book.author}</p>
            <p><strong>Publisher:</strong> {book.publisher || 'N/A'}</p>
            <p><strong>Language:</strong> {book.language || 'N/A'}</p>
            <p><strong>Genre:</strong> {book.genre || 'N/A'}</p>
            <p><strong>Summary:</strong> {book.description || 'No summary available'}</p>
          </div>
          <div className="book-actions">
            <button 
              className="add-to-cart-btn"
              onClick={() => addToCart(book)}
            >
              Add to Cart
            </button>
            <Link to="/books" className="back-btn">Back to Books</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BookDetail;
