// src/components/employee/BookManager.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import BookForm from './BookForm';
import * as api from '../../services/api';

function BookManager() {
  const [books, setBooks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const { isEmployee } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isEmployee) {
      navigate('/'); // Nếu không phải employee, chuyển về trang chủ
    } else {
      fetchBooks();
    }
  }, [isEmployee, navigate]);

  const fetchBooks = async () => {
    try {
      const response = await api.getBooks();
      setBooks(response.data);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const handleAddBook = async (book) => {
    await api.addBook(book);
    setShowForm(false);
    fetchBooks();
  };

  const handleEditBook = async (book) => {
    await api.updateBook(book.id, book);
    setEditingBook(null);
    fetchBooks();
  };

  const handleDeleteBook = async (id) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      await api.deleteBook(id);
      fetchBooks();
    }
  };

  return (
    <div className="book-manager">
      <div className="manager-header">
        <h2>Book Inventory</h2>
        <button 
          className="add-book-btn"
          onClick={() => setShowForm(true)}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <line x1="12" y1="5" x2="12" y2="19" />
            <line x1="5" y1="12" x2="19" y2="12" />
          </svg>
          Add New Book
        </button>
      </div>

      {(showForm || editingBook) && (
        <BookForm
          book={editingBook}
          onSubmit={editingBook ? handleEditBook : handleAddBook}
          onCancel={() => {
            setShowForm(false);
            setEditingBook(null);
          }}
        />
      )}

      <div className="books-table">
        <div className="table-header">
          <span>Title</span>
          <span>Author</span>
          <span>Price</span>
          <span>Quantity</span>
          <span>Genre</span>
          <span>Publisher</span>
          <span>Language</span>
          <span>Actions</span>
        </div>
        {books.map((book) => (
          <div key={book.id} className="table-row">
            <span>{book.title}</span>
            <span>{book.author}</span>
            <span>{book.price.toLocaleString('vi-VN')} VND</span>
            <span>{book.quantity || 0}</span>
            <span>{book.genre || 'N/A'}</span>
            <span>{book.publisher || 'N/A'}</span>
            <span>{book.language || 'N/A'}</span>
            <div className="table-actions">
              <button 
                className="edit-book-btn"
                onClick={() => setEditingBook(book)}
              >
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" />
                  <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z" />
                </svg>
              </button>
              <button 
                className="delete-book-btn"
                onClick={() => handleDeleteBook(book.id)}
              >
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M3 6h18M8 6V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2m3 0v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6h14z"/>
                </svg>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookManager;
