// src/services/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Thay đổi URL backend khi cần

export const getBooks = () => axios.get(`${API_URL}/books`);
export const addBook = (book) => axios.post(`${API_URL}/books`, book);
export const updateBook = (id, book) => axios.put(`${API_URL}/books/${id}`, book);
export const deleteBook = (id) => axios.delete(`${API_URL}/books/${id}`);
export const loginUser = (credentials) => axios.post(`${API_URL}/auth/login`, credentials);
export const registerUser = (userData) => axios.post(`${API_URL}/auth/register`, userData);
