import React, { createContext, useState, useEffect, useContext } from 'react';
import * as api from '../services/api';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [isEmployee, setIsEmployee] = useState(false);

  useEffect(() => {
    const storedUser = sessionStorage.getItem('user');
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      console.log('Restored user:', parsedUser);
      if (parsedUser.role) {
        parsedUser.role = parsedUser.role.toLowerCase();
        setUser(parsedUser);
        setIsAdmin(parsedUser.role === 'admin');
        setIsEmployee(parsedUser.role === 'employee');
      } else {
        console.log('No role found in stored user');
        sessionStorage.removeItem('user');
      }
    } else {
      console.log('No user found in sessionStorage');
    }
  }, []);

  const login = async (credentials) => {
    try {
      const response = await api.loginUser(credentials);
      const userData = response.data;
      console.log('Full API response:', response);
      console.log('User data:', userData);
      if (!userData || !userData.role) {
        throw new Error('User role not provided by API');
      }
      userData.role = userData.role.toLowerCase();
      setUser(userData);
      setIsAdmin(userData.role === 'admin');
      setIsEmployee(userData.role === 'employee');
      sessionStorage.setItem('user', JSON.stringify(userData));
      localStorage.removeItem('cartItems');
      return userData;
    } catch (error) {
      console.error('Login error:', error);
      throw new Error('Login failed');
    }
  };

  const logout = () => {
    setUser(null);
    setIsAdmin(false);
    setIsEmployee(false);
    sessionStorage.removeItem('user');
    localStorage.removeItem('cartItems');
  };

  return (
    <AuthContext.Provider value={{ user, isAdmin, isEmployee, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
